  animation: slide-up 0.35s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

:root[data-motion='none'] .animate-pulse-glow,
:root[data-motion='none'] .animate-slide-up {
  animation: none !important;
}

:root[data-motion='subtle'] .animate-slide-up {
  animation-duration: 0.45s;
}

:root[data-motion='active'] .animate-slide-up {
  animation-duration: 0.25s;
}

